import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './Chat.css'
const ENDPOINT = "http://localhost:8080";
let socket;


export default function Chat() {

    const [message, setMessage] = useState('');
    //const [messages, setMessages] = useState([]);

    useEffect(() => {
      socket = io(ENDPOINT);
    }, [])

    const sendMessage = (event) => {
      event.preventDefault();
      console.log('tere')

      if(message) {
        socket.emit('send-message', message, () => setMessage(''));
      }
    }

    const handleChatInput = (event) => {
        event.persist();
        setMessage({
          [event.target.name]: event.target.value
        });
      }




      return(
        <div className="chat-messages">
            <div className="chat-messages-show-container">
                    <ul className="chat-messages-show-list" id="message-list">
                    </ul>
            </div>
            <div className="chat-messages-create-container"> 
                <input name="message"      
                    onChange={handleChatInput} className="chat-messages-create-input" type="text" />
                <button type="submit"  className="chat-messages-create-button" onClick={sendMessage} > Send </button>
            </div>
        </div>
    );
}
