import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './Chat.css';

const ENDPOINT = "http://localhost:8080";
let socket;


export default function Chat(props) {

    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [recentMessages, setRecentMessages] = useState([]);
    const [user_name, setUsername] = useState({userName: ''});

    const  user  = props.location.state.userName;


    useEffect(() => {
      socket = io(ENDPOINT);
      setUsername(user);
      socket.emit('join',  user_name );
    }, [user, user_name])

    useEffect(() => {
      socket.on('newChatMessage', message => {
        setMessages(messages => [ ...messages, message ]);
      });
    }, [recentMessages])

    useEffect(() => {
      socket.on('recentMessages', message => {
        setRecentMessages(message);
      })
    }, [recentMessages])


    const handleChatInput = (event) => {
      event.persist();
      setMessage({
        [event.target.name]: event.target.value,
        username: user_name
      });
    }


    const sendMessage = (event) => {
      event.preventDefault();
      if(message) {
        socket.emit('send-message',  message);
      } 
    }

      return(

        <div className="chat-messages">
            <div className="chat-messages-show-container">
              {recentMessages && recentMessages.map(msg => {
                 return <ul className="chat-messages-show-list" id="message-list">
                     <li key={msg.id}>{msg.username} :  { msg.message} </li>

                 </ul>
              }
                )

                }
                {console.log(messages)}


            </div>
            <div className="chat-messages-create-container"> 
                <input name="message"      
                    onChange={handleChatInput} className="chat-messages-create-input" type="text" />
                <button type="submit"  className="chat-messages-create-button" onClick={sendMessage} > Send </button>
            </div>
        </div>
    );
}
