import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import { withRouter, useHistory } from 'react-router-dom';
import './Signup.css'
import axios from 'axios';
//import CircularIndeterminate from '../../shared/progressCircle/ProgressCircle';
import Alert from '@material-ui/lab/Alert';


const SignupForm = () => {

  const [values, setValues] = useState({
    username: '',
    password: '',
    file: null
   });
	const [error, setError] = useState("");
	//const [isLoading, setIsloading] = useState(false);
  const history = useHistory();


  const onHandleChange = (event) => {
		event.persist();
		setValues(values => ({
			...values,
			[event.target.name]: event.target.value
		}));
	}



	const onSubmit = async (event) => {
    event.preventDefault();
    let newUser = values;

    /*try {
			//setIsloading(true);
            const responseData = await axios.post('http://localhost:8080/user/signup', newUser);
			if (responseData) {
        console.log(responseData)


				//history.push({ pathname: `/chat/${userId}/${room}`, state:{userName:userName, room:room} } );
			}

		} catch (err) {
			console.log(err.message)
		}*/

	};

	return (
		<React.Fragment>
        { error &&  <Alert severity="error"> { error } </Alert> }
        <Container> 
          <form  className="LoginForm" onSubmit={onSubmit} >
            <h1>Login</h1>
            <TextField 
             id="input"
             label="username"
             type="text"
             name="username"
             onChange={onHandleChange}
             />
            <TextField
             id="input"
             label="Password"
             type="password"
             name="password"
             onChange={onHandleChange}
             />

            <Button
             className="loginButton"
             color="primary"
             variant="contained"
             type="submit" >
                 SIGN UP
            </Button> 
          </form>
        </Container>
        </React.Fragment>
	);
}

export default withRouter(SignupForm);
