import React from 'react';
import './ChatForm.css'

export default function ChatForm({sendMessage, message, handleChatInput}) {
    return(
        <div className="chat-messages">
            <div className="chat-messages-show-container">
                    <ul className="chat-messages-show-list" id="message-list">
                    </ul>
            </div>
            <div className="chat-messages-create-container"> 
                <input name="message"      
                    onChange={handleChatInput} className="chat-messages-create-input" type="text" />
                <button type="submit"  className="chat-messages-create-button" onClick={e => sendMessage(e)} > Send </button>
            </div>
        </div>
    );
}
