import React, { useState, useRef } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import { withRouter, useHistory } from 'react-router-dom';
import './Signup.css'
import axios from 'axios';
//import CircularIndeterminate from '../../shared/progressCircle/ProgressCircle';
import Alert from '@material-ui/lab/Alert';
import ImageUpload from '../../fileUpload';


const SignupForm = () => {

  const [values, setValues] = useState({
    username: '',
    password: '',


   });

   const [file, setFile] = useState(null);
   const [error, setError] = useState('')

  const history = useHistory();

  const form = useRef(null)


  const onHandleChange = (event) => {
		event.persist();
		setValues(values => ({
			...values,
      [event.target.name]: event.target.value,

    }));
    setFile({file: event.target.files[0]});

  }


	const onSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
        formData.append('myImage',this.state.file);
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        };


    try {
			//setIsloading(true);
            const responseData = await axios.post('http://localhost:8080/user/signup',
             formData, config);
			if (responseData) {
        console.log(responseData)


				//history.push({ pathname: `/chat/${userId}/${room}`, state:{userName:userName, room:room} } );
			}

		} catch (err) {
			console.log(err.message)
		}

	};

	return (
		<React.Fragment>
        { error &&  <Alert severity="error"> { error } </Alert> }
        <Container> 
          <form ref={form}  className="LoginForm" onSubmit={onSubmit} >
            <h1>Login</h1>
            <TextField 
             id="input"
             label="username"
             type="text"
             name="username"
             onChange={onHandleChange}
             />
            <TextField
             id="input"
             label="Password"
             type="password"
             name="password"
             onChange={onHandleChange}
             />


         <input type="file" name="myImage" onChange= {onHandleChange} />

            <Button
             className="loginButton"
             color="primary"
             variant="contained"
             type="submit" >
                 SIGN UP
            </Button> 
          </form>
        </Container>
        </React.Fragment>
	);
}

export default withRouter(SignupForm);
