import React from 'react';
import './ChatForm.css'

export default function ChatForm({sendMessage, message, messages}) {
    return(
        <div className="chat-messages">
            <div className="chat-messages-show-container">
                 {messages && messages.map(msg => {
                    return <ul className="chat-messages-show-list" id="message-list">
                        {msg}
                    </ul>
                })}
            </div>
            <div className="chat-messages-create-container"> 
                <input value={message} className="chat-messages-create-input" type="text" />
                <button  className="chat-messages-create-button" onClick={sendMessage} > Send </button>
            </div>
        </div>
    );
}
