import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import history from 'react-router-dom';
import './Chat.css';
import Axios from 'axios';

const ENDPOINT = "http://localhost:8080";
let socket;


export default function Chat(props) {

    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [recentMessages, setRecentMessages] = useState([]);
    const [user_name, setUsername] = useState('');
    const [room_Id, setRoomId] = useState('');
    const  user  = props.location.state.userName;
    const  room  = props.location.state.room;
    const roomName = history.useParams()
    console.log(roomName)


    useEffect(() => {
      socket = io(ENDPOINT);
      setUsername(user);
      setRoomName(room);
      socket.emit('join',{user_name, roomName});

    }, [user, user_name, room, roomName]);

    useEffect(()=> {
      if(roomName) {
        Axios.get(`http://localhost:8080/user/${roomName}`)
        .then(res => {
          if(res) {
            console.log(res)
             setRoomId(res.data.existingUser._id)
            //let roomId = res.data.existingUser._id;
            //setRoomId(roomId)

          }

        })

      }

    }, [roomName]);

    useEffect(() => {
      socket.on('mostRecentMessages', data => {
        setRecentMessages(data.messages)
      })
    }, [recentMessages])

    useEffect(() => {
      socket.on('newChatMessage', message => {
        setMessages(prevMessages => [ ...prevMessages, message ]);
      });
    }, [])

    console.log(room_Id)
    const handleChatInput = (event) => {
      event.persist();
      setMessage({
        [event.target.name]: event.target.value,
        username: user_name,
        roomId: room_Id
      });
    }


    const sendMessage = (event) => {
      event.preventDefault();
      if(message) {
        socket.emit('send-message',  message);
      } 
    }

