import React from 'react';


export default function CurrentMessages({messages, user_name, avatar}) {
    return(
        <React.Fragment>
            {messages && user_name && messages.map(msg => {
                return msg.user_name === user_name ?
                     ( <div key={msg._id} className="messageContainer justifyEnd" >
                          <img src={`http://localhost:8080/${avatar}`} alt={user_name} ></img>
                          <div className="messageBox backgroundBlue">
                            <p className="messageText colorWhite">{msg.message_text}</p>
                          </div>
                        </div>          
                         ):
                         (<div key={msg._id} className="messageContainer justifyStart">
                            <div className="messageBox backgroundLight">
                              <p className="messageText colorDark"> {msg.message_text} </p>
                            </div>
                            <p className="sentText pl-10 ">{msg.user_name}</p>
                          </div>
                         );
            })}
        </React.Fragment>
    );
}
