import React, { useState, useEffect, useContext, useRef } from 'react';
import SocketContext from '../../context/socketContext';
import Container from '@material-ui/core/Container';

import './Chat.css';
import { Grid } from '@material-ui/core';


export default function Chat(props) {

    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [recentMessages, setRecentMessages] = useState([]);
    const [user_name, setUsername] = useState('');
    const [roomName, setRoomName] = useState('');

    const socket = useContext(SocketContext)

    const [loggedIn, setLoggedIn] = useState('');
    const  user  = props.location.state.userName;
    const  room  = props.location.state.room;

    useEffect(() => {
      setUsername(user);
      setRoomName(room);
      socket.emit('join', user_name, roomName);
    }, [user, user_name, room, roomName, socket]);



    useEffect(() => {

      socket.on('broadcast', data => {
        setLoggedIn(data.message_text)
      });



    }, [user, user_name, room, roomName, socket]);




    useEffect(() => {
      socket.on('mostRecentMessages', data => {
        setRecentMessages(data)
      })


    }, [recentMessages, socket])

    useEffect(() => {
      socket.on('newChatMessage', message => {
        setMessages(prevMessages => [ ...prevMessages, message ]);


      });

    }, [socket])


    const handleChatInput = (event) => {
      setMessage({
        [event.target.name]: event.target.value,
        username: user_name,
        room: roomName,
      });
    }


    let sendMessage = useRef();
    let disconnectU = useRef();
    let chatContainer = useRef();

    const scrollToMyRef = () => {
      const scroll =
        chatContainer.current.scrollHeight -
        chatContainer.current.clientHeight;
        chatContainer.current.scrollTo(0, scroll);
    };


    useEffect(() => {
        sendMessage.current = () => {
        socket.emit('send-message',roomName, message, () => scrollToMyRef())
      }  
    },[socket, message, roomName])

    useEffect(() => { 
      disconnectU.current = () => {
        socket.emit('leave',roomName, user_name);
      }


    },[socket, user_name, roomName])

    useEffect(() => {
      window.onunload = async() => {
        socket.emit('leave',roomName, user_name);

      }

    }, [socket, roomName, user_name])





    return(
        <Container>
          <Grid id="top-row" container spacing={24}>
          <Grid className="userList" item xs={3}>
            <p>tere</p>

          </Grid>
          <Grid item xs={7}>
          <h3> {loggedIn} </h3>

        <div >
        <div>
                <button onClick={disconnectU.current}> Log out </button>
              </div>
            <div className="chat-messages-show-container" >



                <ul className="chat-messages-show-list" id="message-list" ref={chatContainer}>
                <React.Fragment>

{recentMessages && user_name && recentMessages.map(msg => {
   return msg.username === user_name ?

       ( 
         <div className="messageContainer justifyEnd">
         <p className="sentText pr-10">{msg.username}</p>
         <div className="messageBox backgroundBlue">
           <p className="messageText colorWhite">{msg.message}</p>
         </div>
       </div>          
        ):

       (
         <div className="messageContainer justifyStart">
           <div className="messageBox backgroundLight">
             <p className="messageText colorDark"> {msg.message} </p>
           </div>
           <p className="sentText pl-10 ">{msg.username}</p>
         </div>
       )


       })}
       </React.Fragment>
                 </ul>

                <ul className="chat-messages-show-list" id="message-list">
                <React.Fragment>

                 {messages && user_name && messages.map(msg => {
                    return msg.user_name === user_name ?

                        ( 
                          <div className="messageContainer justifyEnd">
                          <p className="sentText pr-10">{msg.user_name}</p>
                          <div className="messageBox backgroundBlue">
                            <p className="messageText colorWhite">{msg.message_text}</p>
                          </div>
                        </div>          
                         ):

                        (
                          <div className="messageContainer justifyStart">
                            <div className="messageBox backgroundLight">
                              <p className="messageText colorDark"> {msg.message_text} </p>
                            </div>
                            <p className="sentText pl-10 ">{msg.user_name}</p>
                          </div>
                        )


                        })}
                        </React.Fragment>
                 </ul>



            </div>
            <div className="chat-messages-create-container"> 
                <input name="message"      
                    onChange={handleChatInput} className="chat-messages-create-input" type="text" />
                <button type="submit"  className="chat-messages-create-button" onClick={e => sendMessage.current()} > Send </button>
            </div>
        </div>
        </Grid>
        </Grid>
        </Container>
    );
}
