import React from 'react';


export default function RecentMessages({recentMessages, user_name, avatar}) {

    return(
        <React.Fragment>
            {recentMessages && user_name && recentMessages.map(msg => {
                return msg.username === user_name ?
                    (<div key={msg._id} className="messageContainer justifyEnd" >
                        <Avatar src={`http://localhost:8080/${avatar}`} alt={user_name} />
                        <div className="messageBox backgroundBlue">
                            <p className="messageText colorWhite"> {msg.message} </p>
                        </div>
                     </div>          
                    ):
                    (<div key={msg._id} className="messageContainer justifyStart">
                        <div className="messageBox backgroundLight">
                            <p className="messageText colorDark"> {msg.message} </p>
                        </div>
                        <Avatar src={`http://localhost:8080/${avatar}`} alt={user_name} />
                        </div>);
                })
            }
        </React.Fragment>
    );
}
