import React, { useState, useEffect } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import { withRouter, useHistory } from 'react-router-dom';
import './Signup.css'
import axios from 'axios';
import { useForm } from "react-hook-form";
//import CircularIndeterminate from '../../shared/progressCircle/ProgressCircle';
import Alert from '@material-ui/lab/Alert';


const SignupForm = () => {

  const {user, setUser} = useState({
    username: '',
    password: '',
    selectedFile: ''
   });
	const [error, setError] = useState("");
	//const [isLoading, setIsloading] = useState(false);
  const history = useHistory();

  useEffect(() => {
    register({ name: 'file' }, { required: true }) // still have validation for required
  }, [register])

  const handleChange = (event) => {
    setUser({
      username: event.target.value,
      password: event.target.value,
      selectedFile: event.target.files[0],

    }); // you get all the files object here
  }



	const onSubmit = async () => {
    let user = user;
    console.log(user)
		try {
			//setIsloading(true);
            const responseData = await axios.post('http://localhost:8080/user/signup', user);
			if (responseData) {
        console.log(responseData)


				//history.push({ pathname: `/chat/${userId}/${room}`, state:{userName:userName, room:room} } );
			}

		} catch (err) {
			console.log(err.message)
		}

	};

	return (
		<React.Fragment>
        { error &&  <Alert severity="error"> { error } </Alert> }
        <Container> 
          <form  className="LoginForm" onSubmit={handleSubmit(onSubmit)} >
            <h1>Login</h1>
            <TextField 
             id="input"
             label="username"
             type="text"
             name="username"
             defaultValue=""
             inputRef={register({ required: true, minLength: 6 })}
             />
            <TextField
             id="input"
             label="Password"
             type="password"
             name="password"
             defaultValue=""
             inputRef={register({ required: true, minLength: 6 })}
             />
            <TextField 
             id="input"
             label="Avatar"
             type="file"
             name="avatar"
             inputRef={register({ name: 'file' }, { required: true })}
             onChange={handleChange}
             />
            <Button
             className="loginButton"
             color="primary"
             variant="contained"
             type="submit" >
                 SIGN UP
            </Button> 
          </form>
        </Container>
        </React.Fragment>
	);
}

export default withRouter(SignupForm);
