import React from 'react';
import axios from 'axios';

import './ChatForm.css'

export default function ChatForm({message, handleChatInput}) {

    const  sendMessage = async (event) => {
        event.preventDefault();
         try {
             const responseData = await axios.post('http://localhost:8080/chat/messages', {composedMessage: message});
             console.log(responseData);

         }
         catch(err) {
             console.log(err.message); 
       }
     } 
    return(
        <div className="chat-messages">
            <div className="chat-messages-show-container">
                    <ul className="chat-messages-show-list" id="message-list">
                    </ul>
            </div>
            <div className="chat-messages-create-container"> 
                <input name="message"      
                    onChange={handleChatInput} className="chat-messages-create-input" type="text" />
                <button type="submit"  className="chat-messages-create-button" onClick={sendMessage} > Send </button>
            </div>
        </div>
    );
}
