import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './Chat.css';

const ENDPOINT = "http://localhost:8080";
let socket;


export default function Chat(props) {


    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [recentMessages, setRecentMessages] = useState([]);
    const [user_name, setUsername] = useState({userName: ''});



    const  user  = props.location.state.userName;



    useEffect(() => {
      socket = io(ENDPOINT);
      setUsername(user);
      socket.emit('add user',  user_name );
    }, [user, user_name]);

    useEffect(()=> {
      socket.on('broadcast', data => {
        return setMessage(data.description)
      })
    }, [user_name])



    useEffect(() => {
      socket.on('mostRecentMessages', message => {
        setRecentMessages(message);
      })
    }, [recentMessages])

    useEffect(() => {
      socket.on('newChatMessage', message => {
        setMessages(prevMessages => [ ...prevMessages, message ]);
      });
    }, [])


    const handleChatInput = (event) => {
      event.persist();
      setMessage({
        [event.target.name]: event.target.value,
        username: user_name
      });
    }


    const sendMessage = (event) => {
      event.preventDefault();
      if(message) {
        socket.emit('send-message',  message);
      } 
    }

      return(

        <div className="chat-messages">
            <div className="chat-messages-show-container">
              {recentMessages && recentMessages.map(msg => {
                 return <ul className="chat-messages-show-list" id="message-list">
                     <li key={msg.id}>{msg.username} :  { msg.message} </li>

                 </ul>
              }
                )

                }
                <ul className="chat-messages-show-list" id="message-list">
                {messages && messages.map(msg => {
                 return <li key={msg._id}>{msg.user_name} :  { msg.message_text} </li>


              }
                )

                }
                 </ul>


            </div>
            <div className="chat-messages-create-container"> 
                <input name="message"      
                    onChange={handleChatInput} className="chat-messages-create-input" type="text" />
                <button type="submit"  className="chat-messages-create-button" onClick={e => sendMessage(e)} > Send </button>
            </div>
        </div>
    );
}
