import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import { withRouter, useHistory } from 'react-router-dom';
import './Signup.css'
import axios from 'axios';
import { useForm } from "react-hook-form";
//import CircularIndeterminate from '../../shared/progressCircle/ProgressCircle';
import Alert from '@material-ui/lab/Alert';


const SignupForm = () => {

  const { register, handleSubmit } = useForm();
  const [file, setFile] = useState();
	const [error, setError] = useState("");
	//const [isLoading, setIsloading] = useState(false);
  //const history = useHistory();

  const onSetImage = (event) => {
    event.preventDefault();
    setFile({selectedFile: event.target.files[0]})

  }

  console.log(file)
	const onSubmit = async (event) => {
    const data = new FormData()
   data.append('file', file)

   for(var pair of data.entries()) {
    console.log(pair[0]+ ', '+ pair[1]); 
 }
		try {
			//setIsloading(true);
			const responseData = await axios.post('http://localhost:8080/user/upload', data);
			if (responseData) {
        console.log(responseData)


				//history.push({ pathname: `/chat/${userId}/${room}`, state:{userName:userName, room:room} } );
			}

		} catch (err) {
			console.log(err.message)
		}

	};

	return (
		<React.Fragment>
        { error &&  <Alert severity="error"> { error } </Alert> }
        <Container> 
          <form  className="LoginForm" onSubmit={handleSubmit(onSubmit)} >
            <h1>Signup</h1>
            <TextField 
             id="input"
             label="username"
             type="text"
             name="username"
             defaultValue=""
             inputRef={register({ required: true, minLength: 2 })}
             />
            <TextField
             id="input"
             label="Password"
             type="password"
             name="password"
             defaultValue=""
             inputRef={register({ required: true, minLength: 2 })}
             />
             <input type="file" name="file" onChange={onSetImage} ></input>
            <Button
             className="loginButton"
             color="primary"
             variant="contained"
             type="submit" >
                 SIGN UP
            </Button> 
          </form>
        </Container>
        </React.Fragment>
	);
}

export default withRouter(SignupForm);
