import React from 'react';


export default function Message({user_name, username, message, avatar}) {
    return(
        <React.Fragment>
        {username === user_name ?
            <div key={msg._id} className="messageContainer justifyEnd" >
                <Avatar src={`http://localhost:8080/${avatar}`} alt={user_name} />
                <div className="messageBox backgroundBlue">
                    <p className="messageText colorWhite"> {message} </p>
                </div>
            </div>          
        : <div key={id} className="messageContainer justifyStart">
            <div className="messageBox backgroundLight">
                <p className="messageText colorDark"> {message} </p>
            </div>
                <Avatar src={`http://localhost:8080/${avatar}`} alt={user_name} />
            </div>}
        </React.Fragment>
    );

}
