import React, { useState, useEffect, useContext, useRef } from 'react';
import SocketContext from '../../../context/socketContext';
import Container from '@material-ui/core/Container';
import './Chat.css';
import { Grid, Button } from '@material-ui/core';
import RecentMessages from '../../components/RecentMessages/RecentMessages';
import CurrentMessages from '../../components/currentMessages/CurrentMessages';
import UserList from '../../components/users/UserList';
import CloseIcon from '@material-ui/icons/Close';



export default function Chat(props) {

    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [recentMessages, setRecentMessages] = useState([]);
    const [users, setUsers] =useState([])
    const [user_name, setUsername] = useState('');
    const [roomName, setRoomName] = useState('');
    const [userAvatar, setUserAvatar] = useState('');


    const socket = useContext(SocketContext)

    const [loggedIn, setLoggedIn] = useState('');
    const  user  = props.location.state.userName;
    const  room  = props.location.state.room;
    const  avatar = props.location.state.avatar;

    useEffect(() => {
      setUsername(user);
      setRoomName(room);
      setUserAvatar(avatar);
      const socket = io.connect(SOCKET_URL);
      socket.emit('join', user_name, roomName);
    }, [user, user_name, room, roomName, socket, avatar]);




    useEffect(() => {

      socket.on('broadcast', data => {
        setLoggedIn(data.message_text)
      });



    }, [user, user_name, room, roomName, socket]);




    useEffect(() => {
      socket.on('mostRecentMessages', data => {
        setRecentMessages(data)
      })


    }, [recentMessages, socket])

    useEffect(() => {
      socket.on('usersInRoom', users => {
        console.log(users)
        setUsers(users);
      })


    }, [ socket])

    useEffect(() => {
      socket.on('newChatMessage', message => {
        setMessages(prevMessages => [ ...prevMessages, message ]);


      });

    }, [socket])


    const handleChatInput = (event) => {
      setMessage({
        [event.target.name]: event.target.value,
        username: user_name,
        avatar: userAvatar,
        room: roomName,
      });
    }


    let sendMessage = useRef();
    let disconnectU = useRef();

    useEffect(() => {
        sendMessage.current = () => {
        socket.emit('send-message',roomName, message, avatar)
      }  
    },[socket, message, roomName, avatar])

    useEffect(() => { 
      disconnectU.current = async () => {
        socket.emit('leave',roomName, user_name);
      }


    },[socket, user_name, roomName])

    useEffect(() => {
      window.onunload = async() => {
        socket.emit('leave',roomName, user_name);

      }

    }, [socket, roomName, user_name])


    return(
        <Container>
          <Grid id="top-row" container item xl={12}>
            <Grid className="userList" item ={3}>
            <UserList  users={users}/>
            </Grid>
          <Grid item xl={6}>
            <div className="admin_message">
              <h3> {loggedIn} </h3>
              <Button onClick={disconnectU.current} ><CloseIcon /></Button> 
            </div>
            <div >
              <div className="chat-messages-show-container"  >
                <div className="chat-messages-show-list" id="message-list"   >
                      <RecentMessages recentMessages={recentMessages} user_name={user_name} avatar={avatar} />
                 </div>
                <div className="chat-messages-show-list" id="message-list" >
                      <CurrentMessages messages={messages} user_name={user_name} avatar={avatar} />
                </div>   
            </div>
            <div className="chat-messages-create-container"> 
                <input name="message"      
                    onChange={handleChatInput}
                    className="chat-messages-create-input"
                    type="text" />
                <button
                  type="submit"
                  className="chat-messages-create-button"
                  onClick={e => sendMessage.current()} > Send </button>
            </div>
        </div>
        </Grid>
        </Grid>
      </Container>
    );
}
