import React, { useState, useEffect, useCallback } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import './Chat.css';

const ENDPOINT = "http://localhost:8080";
let socket;


export default function Chat(props) {

    const [message, setMessage] = useState('');
    //const [messages, setMessages] = useState([]);
    //const [recentMessages, setRecentMessages] = useState([]);
    const [user_name, setUsername] = useState('');
    const [roomName, setRoomName] = useState('');


    const [loggedIn, setLoggedIn] = useState('');
    const  user  = props.location.state.userName;
    const  room  = props.location.state.room;


    useEffect(() => {
      socket = io(ENDPOINT);
      setUsername(user);
      setRoomName(room);

    }, [user, user_name, room]);

    useEffect(()=> {
      socket.on('user joined', data => {
        setLoggedIn(data.message_text)
      })
    }, []);

    const getChannelConversations = useCallback(() => {
      axios.get(`http://localhost:8080/chat/${roomName}`)
      .then(res => {
        console.log(res)


        socket.emit('join', roomName, roomName);


      })
      .catch(error => {
        console.log(error)
      })
    }, [roomName])

    useEffect(() => {
      getChannelConversations();

    }, [getChannelConversations])

    axios.post('http://localhost:8080/' + roomName,  {inputMessage: message})
    .then(res => {
      const socketMsg = {
        message: message,
        author: user_name,
      }
      socket.emit('new message', socketMsg)
      setMessage('')

    })
    .catch(err => {
      console.log(err)
    })




    const handleChatInput = (event) => {
      event.persist();
      setMessage({
        [event.target.name]: event.target.value,
        username: user_name,
      });
    }


    const sendMessage = (event) => {
      event.preventDefault();
      if(message) {
        socket.emit('send-message',  message);
      } 
    }

      return(
        <div>
          <h3> {loggedIn} </h3>
        <div className="chat-messages">
            <div className="chat-messages-show-container">
            <ul className="chat-messages-show-list" id="message-list">

                 </ul>

                <ul className="chat-messages-show-list" id="message-list">
                {messages && messages.map(msg => {
                 return <li>{msg.user_name} :  { msg.message_text} </li>


              }
                )

                }
                 </ul>


            </div>
            <div className="chat-messages-create-container"> 
                <input name="message"      
                    onChange={handleChatInput} className="chat-messages-create-input" type="text" />
                <button type="submit"  className="chat-messages-create-button" onClick={e => sendMessage(e)} > Send </button>
            </div>
        </div>
        </div>
    );
}
