import React, { useState, useEffect } from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Container from '@material-ui/core/Container';
import { withRouter, useHistory } from 'react-router-dom';
import './Signup.css'
import axios from 'axios';
//import CircularIndeterminate from '../../shared/progressCircle/ProgressCircle';
import Alert from '@material-ui/lab/Alert';


const SignupForm = () => {

  const [values, setValues] = useState({
    username: '',
    password: '',

   });
   const [file, setFile] = useState(null);
	const [error, setError] = useState("");
	//const [isLoading, setIsloading] = useState(false);
  const history = useHistory();


  const onHandleChange = (event) => {
		event.persist();
		setValues(values => ({
			...values,
      [event.target.name]: event.target.value,

    }));

  }

  const onSetFile = (event) => {
    setFile({
      file: event.target.files[0],
      loaded: 0,
    })

  }


	const onSubmit = async (event) => {
    event.preventDefault();
    let newUser = values;
    let newFile = file;
    console.log(newFile)

    try {
			//setIsloading(true);
            const responseData = await axios.post('http://localhost:8080/user/signup', newUser);
			if (responseData) {
        console.log(responseData)


				//history.push({ pathname: `/chat/${userId}/${room}`, state:{userName:userName, room:room} } );
			}

		} catch (err) {
			console.log(err.message)
		}

	};

	return (
		<React.Fragment>
        { error &&  <Alert severity="error"> { error } </Alert> }
        <Container> 
          <form  className="LoginForm" onSubmit={onSubmit} >
            <h1>Login</h1>
            <TextField 
             id="input"
             label="username"
             type="text"
             name="username"
             onChange={onHandleChange}
             />
            <TextField
             id="input"
             label="Password"
             type="password"
             name="password"
             onChange={onHandleChange}
             />
              <TextField
             id="input"
             label="Avatar"
             type="file"
             name="file"
             onChange={onSetFile}
             />

            <Button
             className="loginButton"
             color="primary"
             variant="contained"
             type="submit" >
                 SIGN UP
            </Button> 
          </form>
        </Container>
        </React.Fragment>
	);
}

export default withRouter(SignupForm);
