import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './Chat.css';

const ENDPOINT = "http://localhost:8080";
let socket;


export default function Chat(props) {

    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [recentMessages, setRecentMessages] = useState();
    const [user_name, setUsername] = useState('');
    const [users, setUsers] = useState([]);   

    const  user  = props.location.state.userName;


    useEffect(() => {
      socket = io(ENDPOINT);
      setUsername(user);
      socket.emit('join',  user_name );
    }, [user, user_name])

    useEffect(() => {
      socket.on('mostRecentMessages', recentMessages => {
        setRecentMessages(recentMessages);
      })
    }, [recentMessages])

    useEffect(()=> {
      socket.on('newChatMessage', message => {
        return setMessages([ ...messages, message ]) 
      });

    }, [messages, users, user_name])

    const sendMessage = (event) => {
      event.preventDefault();
      if(message) {
        console.log(message)
        console.log(user_name)
        socket.emit('send-message',  message, user_name);
      }


    }

    const handleChatInput = (event) => {
        event.persist();
        setMessage({
          [event.target.name]: event.target.value
        });
      }

      return(
        <div className="chat-messages">
            <div className="chat-messages-show-container">
              {recentMessages && recentMessages.map(msg => {
                 return <ul className="chat-messages-show-list" id="message-list">
                    <li> {msg.message} </li>

                 </ul>
              }
                )}
                {messages && messages.map(msg => {
                 return <ul className="chat-messages-show-list" id="message-list">
                    <li key={msg.id}>{user_name} :  { msg.message_text} </li>

                 </ul>
              }
                )}




            </div>
            <div className="chat-messages-create-container"> 
                <input name="message"      
                    onChange={handleChatInput} className="chat-messages-create-input" type="text" />
                <button type="submit"  className="chat-messages-create-button" onClick={sendMessage} > Send </button>
            </div>
        </div>
    );
}
